/*Data for the table `sipk_jurusan_pt` */

insert  into `sipk_jurusan_pt`(`id`,`jurusan`,`deskripsi`,`kemampuan_penunjang`,`bidang_minat`,`lapangan_kerja`,`ptn_Sasaran`,`state`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`publish_up`,`publish_down`,`attribs`,`version`,`hits`) values (1,'Arsitektur','Arsitektur adalah ilmu merancang bangunan yang mengoptimalkan seni yang kreatif imaginative berdasarkan teknologi untuk memberikan pemanfaatan ruang yang nyaman dan indah bagi aktivitas manusia dan lingkungannya. ',' Matematika (Skala & Konsep tiga dimensi)\r\n Seni dan kemampuan menggambar berskala ',' Arsitektur pemukiman & Hunian\r\n Ilmu & Teknologi Arsitektur\r\n Struktur Bangunan Terpadu\r\n Studio perancangan arsitektur\r\n Teknologi & Perencanaan bangunan ',' Arsitek\r\n (konsultan perancangan bangunan)\r\n Pegawai Negeri pada BAPENAS, Dinas PU\r\n Bank sebagai penilai kelayakan KPR ','unsyiah, usu, unsri, ipb, itb, uns, ui, ugm, ipb, itb, uns, ui, ugm, unlam, untad, unsrat',0,'0000-00-00 00:00:00',0,'11','0000-00-00 00:00:00',0,'0000-00-00 00:00:00','0000-00-00 00:00:00','',0,0),(2,'Arsitektur Lansekap ','<p>Arsitektur Lansekap adalah ilmu tentang perancangan arsitektur pertamanan ( PRA ) dan perencanaan arsitektur pertamanan ( PRE ) untuk menghasilkan tata ruang taman yang indah dan terpadu dengan lingkungan untuk daerah rekreasi, hotel, resort, daerah cagar budaya, daerah industry dan agrowisata.</p>','<ul>\r\n<li>Matematika (skala dan konsep tiga dimensi)</li>\r\n<li>Seni dan kemampuan menggambar berskala</li>\r\n</ul>','<ul>\r\n<li>Manajemen produksi tanaman</li>\r\n<li>Tanaman obat &amp; karbihidrat non biji</li>\r\n<li>Tataniaga pertanian</li>\r\n<li>Teknik perkebunan</li>\r\n</ul>','<ul>\r\n<li>Konsultan atau kontraktor pengelolaan lansekap</li>\r\n<li>Departemen DU</li>\r\n<li>Departemen pariwisata</li>\r\n</ul>','<p>ipb</p>',1,'2012-08-06 01:39:50',0,'','0000-00-00 00:00:00',0,'0000-00-00 00:00:00','0000-00-00 00:00:00','',0,0),(3,'Ilmu Keperawatan','<p>Ilmu Keperawatan mempelajari tentang hal-hal yang berhubungan dengan tata cara merawat orang sakit dengan benar.</p>','<ul>\r\n<li>Kedisiplinan</li>\r\n<li>Sikap empati</li>\r\n<li>Keterampilan berkomunikasi</li>\r\n</ul>','<ul>\r\n<li>Ilmu keperawatan</li>\r\n<li>Profesi keperawatan</li>\r\n</ul>','<p>Lulusannya dibutuhkan dirumah sakit dan instansi kesehatan baik sebagai perawat atau manajer, bisa juga sebagai dosen.</p>','<p>unsyiah, usu, unand, unsri, ui, unpad, undip, unair, unibraw, unhas, unej</p>',1,'2012-08-06 01:42:00',0,'','0000-00-00 00:00:00',0,'0000-00-00 00:00:00','0000-00-00 00:00:00','',0,0),(4,'Astronomi','<p>Astronomi adalah cabang ilmu pengetahuan yang menyelidiki benda dan isi jagad raya. Kurikulum yang dikaji di jurusan ini antara lain, Kosmologi, struktur galaksi dan struktur Bintang. Astronomi erat hubungannya dengan fisika dan matematika, oleh karena itu pendidikan tingkat dasar di jurusan Astronomi terdiri tidak kurang dari 75% pelajaran fisika dan matematika, semakin tinggi kuliahnya, makin kurang komponen tersebut dan ramuannya menjadi terbalik, dalam arti 75% terdiri dari mata kuliah astronomi dan 25% terdiri mata kuliah pilihan (fisika, matematika atau bidang lainnya yang dipandang perlu).</p>','<p>Untuk menjadi mahasiswa astronomi, diperlukan kemampuan logika dan rasa ingin tahu yang besar karena banyak kegiatan yang berkaitan dengan laboratorium dan observatorium boscha, serta mampu berbahasa inggris karena buku referensi kuliah abnyak menggunakan bahasa inggris sebagai pengantar.</p>','<ul>\r\n<li>Astronomi plasma</li>\r\n<li>Astronomi relativistic</li>\r\n<li>Atmosfer bintang</li>\r\n<li>Benda kecil dalam tata surya</li>\r\n<li>Benda ganda</li>\r\n<li>Dinamika system antarbintang</li>\r\n<li>Evolusi bintang</li>\r\n<li>Lintasan satelit</li>\r\n<li>Materi antar bintang</li>\r\n</ul>','<ul>\r\n<li>Planetarium</li>\r\n<li>LAPAN</li>\r\n<li>Angkatan Laut</li>\r\n<li>Dephub</li>\r\n<li>Dephankam</li>\r\n<li>BPPT</li>\r\n<li>Industry satelit</li>\r\n<li>Perkomputasian, dll.</li>\r\n</ul>','<p>itb</p>',1,'2012-08-06 01:43:42',0,'','0000-00-00 00:00:00',0,'0000-00-00 00:00:00','0000-00-00 00:00:00','',0,0),(5,'Teknik Informatika','<p>Teknik Informatika adalah kumpulan disiplin yang mengolah fakta simbolik dengan menggunakan hardware (computer), software (perangkat lunak) dan brainware (pemakai). Kelompok bidang keahlian teknik informatika adalah rekayasa perangkat lunak dan system informasi, grafik dan intelegensia buatan, system jaringan computer terdistribusi, control robotika dan computer aided X.</p>','<ul>\r\n<li>Logika</li>\r\n<li>Algoritma (berfikir sistematis)</li>\r\n<li>Memiliki kognitif yang baik</li>\r\n<li>Menyenangi teknologi informasi</li>\r\n</ul>','<ul>\r\n<li>Informarika teoritis</li>\r\n<li>Rekayasa perangkat lunak &amp; system informasi</li>\r\n<li>System terdistribusi (network)</li>\r\n</ul>','<ul>\r\n<li>Konsultan rancang bangun system perangkat lunak</li>\r\n<li>Industry perangkat lunak (software)</li>\r\n<li>Perusahaan atau instansi pemerintah pada system informasi dan pengolahan data</li>\r\n<li>Control otomatik</li>\r\n</ul>','<p>unib , itb , its</p>',1,'2012-08-06 01:45:41',0,'','0000-00-00 00:00:00',0,'0000-00-00 00:00:00','0000-00-00 00:00:00','',0,0);

